$(document).ready(function () {
  $.getJSON("https://voltiumwallet.com/api/txn.php", function (t) {
    var d = "";
    $.each(t, function (t, o) {
      (d += "<tr>"),
        (d += '<td><a href="hash/">' + o.blockhash + "</a></td>"),
        (d += '<td style="color: #f5f5f5;">' + o.blocktime + "</td>"),
        (d += '<td style="color: #f5f5f5;">' + o.address + "</td>"),
        (d += '<td style="color: #f5f5f5;">' + o.amount + "</td>"),
        (d +=
          '<td style="color: #f5f5f5;"><div class="badge badge-outline-success">Approved</div></td>'),
        (d += "</tr>");
    }),
      $("#data").append(d);
  });
});
